#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="V:\Projects\expressJs\test-express-app\node_modules\.pnpm\acorn@7.4.1\node_modules\acorn\bin\node_modules;V:\Projects\expressJs\test-express-app\node_modules\.pnpm\acorn@7.4.1\node_modules\acorn\node_modules;V:\Projects\expressJs\test-express-app\node_modules\.pnpm\acorn@7.4.1\node_modules;V:\Projects\expressJs\test-express-app\node_modules\.pnpm\node_modules;V:\Projects\expressJs\test-express-app\node_modules;V:\Projects\expressJs\node_modules;V:\Projects\node_modules;V:\node_modules;V:\projects\expressJs\test-express-app\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/v/Projects/expressJs/test-express-app/node_modules/.pnpm/acorn@7.4.1/node_modules/acorn/bin/node_modules:/mnt/v/Projects/expressJs/test-express-app/node_modules/.pnpm/acorn@7.4.1/node_modules/acorn/node_modules:/mnt/v/Projects/expressJs/test-express-app/node_modules/.pnpm/acorn@7.4.1/node_modules:/mnt/v/Projects/expressJs/test-express-app/node_modules/.pnpm/node_modules:/mnt/v/Projects/expressJs/test-express-app/node_modules:/mnt/v/Projects/expressJs/node_modules:/mnt/v/Projects/node_modules:/mnt/v/node_modules:/mnt/v/projects/expressJs/test-express-app/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../acorn/bin/acorn" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../acorn/bin/acorn" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../acorn/bin/acorn" $args
  } else {
    & "node$exe"  "$basedir/../acorn/bin/acorn" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
